import UIKit

/// https://pradeepdahiya-34647.medium.com/palindrome-index-hacker-rank-swift-solution-765254325024#:~:text=Given%20a%20string%20of%20lowercase,no%20solution%2C%20return%20%2D1.
/// https://www.hackerrank.com/challenges/palindrome-index/problem

func palindromeIndex(s: String) -> Int {
    let chars = Array(s)
    var i = 0
    var j = chars.count - 1
    while i < j {
        if chars[i] != chars[j] {
            // Try removing char at index i
            if isPalindrome(chars, i + 1, j) {
                return i
            }
            // Try removing char at index j
            if isPalindrome(chars, i, j - 1) {
                return j
            }
            // No solution found
            return -1
        }
        i += 1
        j -= 1
    }
    // String is already a palindrome
    return -1
}

func isPalindrome(_ chars: [Character], _ start: Int, _ end: Int) -> Bool {
    var i = start
    var j = end
    while i < j {
        if chars[i] != chars[j] {
            return false
        }
        i += 1
        j -= 1
    }
    return true
}

